package com.basics;

public class AverageNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num1, num2, num3;

		num1 = 85;
		num2 = 84;
		num3 = 90;

		float avg = (float) (num1 + num2 + num3) / 3;

		System.out.println("The average is :" + avg);
	}

}
