package com.basics;

public class AverageNumber2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		float num1, num2, num3;

		num1 = 85.6f;
		num2 = 84.6f;
		num3 = 90.6f;

		float avg = (num1 + num2 + num3) / 3;

		System.out.println("The average is :" + avg);

	}

}
