package com.basics;

public class SimpleInterest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double p, r, t, si;
		p = 100; // Principle 
		r = 10;  // Rate 
		t = 2;   // Time
		
		si = (p * r *t) / 100; // Simple Interest 
		
		System.out.println(si);
	}

}
