package com.ifelse;

public class OddEven {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num = 89;

		if (num % 2 == 0) {
			System.out.println(num + " is even");
		} else {
			System.out.println(num + " is odd");
		}
	}

}

