public class Voting {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int age = 16;

		if (age >= 18) {
			System.out.println("You can vote");
		} else {
			System.out.println("You cannot vote");
		}
		System.out.println("Voting ends....");
	}

}
